import React, { useState } from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

export default function Register(){
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [msg, setMsg] = useState('')
  const [otp, setOtp] = useState('')
  const [err, setErr] = useState('')
  const [awaitingOtp, setAwaitingOtp] = useState(false)
  const navigate = useNavigate()

  const onRegister = async () => {
    setErr('')
    try{
      const res = await axios.post('/auth/register', { email, password })
      setMsg('Verification OTP sent to your email. Please enter it below to complete registration.')
      if(res.data.otp){ setOtp(res.data.otp) }
      setAwaitingOtp(true)
    }catch(e){ setErr(e?.response?.data?.error || 'Register failed') }
  }

  const onVerify = async () => {
    setErr('')
    try{
      await axios.post('/auth/verify', { email, otp })
      navigate('/login')
    }catch(e){ setErr(e?.response?.data?.error || 'Verify failed') }
  }

  return (
    <div className="max-w-md mx-auto px-6 py-16">
      <h2 className="text-3xl font-extrabold">Create Account</h2>
      <div className="mt-6 space-y-4">
        <input className="w-full bg-white/10 border border-white/10 rounded px-3 py-2" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} />
        <input className="w-full bg-white/10 border border-white/10 rounded px-3 py-2" placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} />
        {err && <div className="text-rose-400 text-sm">{err}</div>}
        {!awaitingOtp && (
          <button onClick={onRegister} className="px-5 py-2 rounded bg-primary">Register</button>
        )}
        {awaitingOtp && (
          <>
            {msg && <div className="text-emerald-400 text-sm">{msg}</div>}
            <input className="w-full bg-white/10 border border-white/10 rounded px-3 py-2" placeholder="Enter OTP" value={otp} onChange={e=>setOtp(e.target.value)} />
            <button onClick={onVerify} className="mt-2 px-5 py-2 rounded bg-secondary">Verify OTP</button>
          </>
        )}
      </div>
    </div>
  )
}
